mapper.select:
SELECT t_user.*,IFNULL((select balance from t_user_wallet where
uid=t_user.uid and t_user_wallet.type=1),0) as integral FROM t_user
WHERE uid = #{uid}

SELECT * FROM t_user WHERE 1 = 1

	AND lower(username) = lower(#{userName})


	AND password = #{passWord}

SELECT * FROM t_user_dissociate WHERE 1 = 1

    AND lower(username) = lower(#{userName})


    AND password = #{passWord}

SELECT * FROM t_reserve_account WHERE 1 = 1

	AND lower(username) = lower(#{userName})


	AND cagent = #{cagent}

mapper.insert:
insert into t_user(username,password,login_ip,reg_ip,ag_username,ag_password,email,vip_level,mobile, cagent,is_daili,is_delete,qk_pwd,reg_date,login_time,wallet,top_uid,is_stop,is_mobile,realname,hg_username,mg_username,type_id,junior_uid,regurl,loginmobile,rmk) values(#{username},#{password},INET_ATON(#{reg_ip}),INET_ATON(#{reg_ip}),#{ag_username},#{ag_password},#{email},#{vip_level},#{mobile},#{cagent},#{is_daili},'0',#{qkpwd},now(),now(), '0',#{top_uid},'0',#{is_mobile},#{realName},
#{hg_username},'' ,
#{usertype}
IFNULL((select a.id typeid from t_user_type a left join t_cagent b on a.cid=b.id where b.cagent=#{cagent} and a.isDefault=0 LIMIT 0,1),0)
,#{junior_uid},#{regurl},#{loginmobile},#{remark});

mapper.update:
		update t_user


			set is_stop = #{is_stop}


			set hg_username = #{hg_username}


			set password = #{password}


			set qk_pwd = #{qkpwd}


			set login_time = NOW() ,login_ip=INET_ATON(#{ip})

        where username = #{userName} ;

mapper.select:
SELECT count(1) cnt,sum(case WHEN t_type='IN' then t_money else
-1*t_money end) total FROM t_transfer where
DATE_FORMAT(t_time,
'%Y-%m-%d') BETWEEN #{bdate} and #{edate}

	AND username = #{userName}


	and type = #{type}


	and t_type = #{t_type}

select t_time,(case when t_type ='IN' then '转入平台' else '转入游戏' end)
t_type,type,old_money,t_money,new_money
from t_transfer where
DATE_FORMAT(t_time, '%Y-%m-%d') BETWEEN #{bdate} and
#{edate}

	AND username = #{userName}


	and type = #{type}


	and t_type = #{t_type}

ORDER BY t_time DESC
LIMIT #{starLimit},#{pageSize}

SELECT count(1) cnt,sum(order_amount) total,trade_status FROM (
select uid,order_time,case pay_type when '1' then '网银' when '2' then
'微信'
else '支付宝' end pay_type,order_amount,
case trade_status when 'paying'
then '处理中' when 'success' then '交易成功' else
'交易失败' end
trade_status,pay_type type
from t_recharge where 1=1

	AND uid = #{uid}


	and DATE_FORMAT(order_time, '%Y-%m-%d') BETWEEN #{bdate}
	and #{edate}

union all
select uid,addtime as order_time,'银行汇款' as pay_type,amount as
order_amount,case `status` when '0' then '处理中' when '1' then '交易成功'
else
'交易失败' end trade_status,'4' as type from t_amount_record  where 1=1

	AND uid = #{uid}


	and DATE_FORMAT(addtime, '%Y-%m-%d') BETWEEN #{bdate} and
	#{edate}
 )a where
DATE_FORMAT(order_time, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}

	AND uid = #{uid}


	and pay_type = #{type}


	and trade_status = #{status}

select * from ( select uid,order_time, case when pay_type=1 or pay_type=21 then '网银' when pay_type=2 or pay_type=22 then '微信' when pay_type=3 or pay_type=23 then '支付宝' when pay_type=4 or pay_type=24 then '财付通' when pay_type=5 or pay_type=25 then '银联扫码' when pay_type=6 or pay_type=26 then '京东支付' when pay_type=7 or pay_type=27 then '快捷支付' when pay_type=8 or pay_type=28 then '微信条码' when pay_type=9 or pay_type=29 then '支付宝条码' when pay_type=10 or pay_type=30 then '扫码支付' when pay_type=11 or pay_type=31 then '银行汇款' when pay_type=12 or pay_type=32 then '百度钱包' else '其他' end pay_type,order_amount, case trade_status when 'paying' then '处理中' when 'success' then '交易成功' else '交易失败' end trade_status,pay_type type,'无' As rmk from t_recharge where 1=1
AND uid = #{uid}
and DATE_FORMAT(order_time, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}
union all select uid,addtime as order_time,'银行汇款' as pay_type,amount as order_amount,case `status` when '0' then '处理中' when '1' then '交易成功' else '交易失败' end trade_status,'4' as type,rmk as rmk from t_amount_record where 1=1
AND uid = #{uid}
and DATE_FORMAT(addtime, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}
)a where 1=1
and pay_type = #{type}
and trade_status = #{status}
ORDER BY order_time DESC
LIMIT #{starLimit},#{pageSize}

SELECT count(1) cnt,sum(amount) total FROM t_withdraw where
DATE_FORMAT(add_time, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}

	AND uid = #{uid}


	AND `status` = #{status}

select a.add_time,a.cardNo as card_num,a.amount,a.remark,a.rmk,poundage,administrative_fee,amount_paid, case a.`status` when '0' then '处理中' when '1' then '已通过' when '2' then '未通过' else '未知' end status from t_withdraw a left join t_user_card b on a.cardNo=b.id where DATE_FORMAT(a.add_time, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}
AND a.uid = #{uid}
AND a.`status` = #{status}
ORDER BY a.add_time DESC LIMIT #{starLimit},#{pageSize}

select SQL_NO_CACHE a.id,a.card_username,b.bank_name,CONCAT(
LEFT(a.card_num,4),'***********',RIGHT(a.card_num,4),'')
card_num,a.card_address from t_user_card a
left join t_card_type b on
a.bank_id=b.bank_id where a.uid = #{uid}

	AND a.`id` = #{id}


	and #{Ttime} =#{Ttime}

Select type,title,img1,img2,img3,src1,weight,rmk,date_format(updatetime,'%Y-%m-%d') updatetime from t_webcom_config where
`status`=1 and type=#{type}

	AND userid=#{cagent}

ORDER BY type,weight desc

Select domain from t_referer_url where 1=1

	AND domain=#{domain}


	AND LOWER(name)=LOWER(#{cagent})

