mapper.select:
SELECT

FROM t_withdraw 
WHERE uid=#{uid} AND add_time BETWEEN #{bdate} AND #{edate}

	AND status = #{status}

ORDER BY add_time DESC
LIMIT ${(pageNo-1)*pageSize},#{pageSize}

SELECT
	count(1) cnt,ifnull(sum(amount),0) total
FROM t_withdraw
WHERE uid=#{uid} AND add_time BETWEEN #{bdate} AND #{edate}

	AND status = #{status}
